/* eslint-disable jsx-a11y/anchor-is-valid */
import React from 'react';
import { connect } from 'react-redux';
import { 
  fetchNewVideos, 
  fetchHotVideos,
  fetchFiveSevenVideos
} from '../actions';

class Header extends React.Component {

  changeActiveTab = (event) => {
    event.stopPropagation();
    this.removeActiveTab();
    if(!event.target.classList.contains('active')) {
      event.target.classList.add('active')
    }
  }

  removeActiveTab = () => {
    let active = document.querySelectorAll('.active')[0]; 
    active.classList.remove('active');
  }

  renderTabs = () => {
    // const tabValues = ['new', 'hot', '5 - 7', '7 - 10', '10 - 15', '30+'];
    const tabValues = [
      {'func': this.fetchNewVideos, 'val': 'new'},
      {'func': this.fetchHotVideos, 'val': 'hot'},
      {'func': this.fetchFiveSevenVideos, 'val': '5 - 7'}
    ];

    return tabValues.map(obj => {
      return (
        <a
          className="item" 
          href="#" 
          onClick={ (event) => {
            this.changeActiveTab(event)
            obj['func']()
          }}>
          {obj['val']}
        </a> 
      );
    });
  }


  render() {
    return (
      <div className="header-comp ui container">
        <div className="header ui segment">
          <h1>ChowTime TV</h1>
          <i className="fas fa-utensils"></i>
          <i className="fas fa-tv"></i>
          <div className="ui toggle checkbox">
            <input type="checkbox" name="public"/>
            <label>Night Mode</label>
          </div>
        </div>
        <div className="ui secondary pointing menu">
          {/* add active classname to change pointer */}
          {this.renderTabs()}
          <a 
            href="#" 
            onClick={() => {
            this.changeActiveTab()
            this.props.fetchNewVideos()
            }}className="active item">new</a> 
          <a href="#" onClick={ () => this.props.fetchHotVideos()} className="item">hot</a>
          <a href="#" onClick={ () => this.props.fetchFiveSevenVideos()} className="item">5 - 7</a>
          <a href="#" onClick={this.changeActiveTab} className="item">7 - 10</a>
          <a href="#" className="item">10 - 15</a>
          <a href="#" className="item">30+</a>
        </div>
      </div>
    );
  }
}


export default connect(null, 
  { fetchNewVideos, 
    fetchHotVideos,
    fetchFiveSevenVideos
  })
  (Header);
