import React from 'react';
import { connect } from 'react-redux';
import { signInStatus } from '../../actions';
import './RedditAuth.scss';

class RedditAuth extends React.Component {
  // constructor(props) {
  //   super();
  // }

  componentDidMount() {
    this.props.signInStatus();
  }

  renderButton() {
    let url = 'https://www.reddit.com/api/v1/authorize?client_id=06_IsJue03S96Q&response_type=code&state=this1is2a3random4string5&redirect_uri=http://localhost:3001/authorize&duration=permanent&scope=vote';
    console.log(this.props.isSignedIn);
    if(!this.props.isSignedIn) {
      return (
        <a className='signin-btn oauth-btn' href={url}>
          Login
        </a>
      );
    } else {
      return (
        <a className='signout-btn oauth-btn' href="loc">
          Logout
        </a>
      );
    }
  }


  render() {
    return (
      <div className="btn-container">
        {this.renderButton()}
      </div>
    );
  };
}

const mapStateToProps = state => ({ isSignedIn: state.isSignedIn.isSignedIn });


export default connect(mapStateToProps, {signInStatus})(RedditAuth);
