const INITIAL_STATE = {
  isSignedIn: null
  // id: null
};

export default (state = INITIAL_STATE, action) => {
  switch(action.type) {
    case 'SIGNIN':
     return {...state, isSignedIn: action.payload };
    case 'LOGOUT':
    return {...state, id: null, isSignedIn: false };
     default:
      return state;
  }
}
